import React, { useState } from 'react';
import './ProductInfo.css';

const ProductInfo = ({ onOpenBrochure }) => {
  const features = [
  { icon: '💡', title: '智慧照明', description: '隧道智能照明系统，节能环保，可按需调光与远程控制' },
  { icon: '🛡️', title: '安全监测', description: '覆盖隧道全域风险监测，包括渗漏水、裂缝、结构变形等' },
  { icon: '⚙️', title: '故障诊断', description: '具备电流、电压、温度、振动等设备状态分析与故障预警功能' },
  { icon: '🌿', title: '绿色节能', description: '低成本高效率照明控制，降低能耗与维护成本' },
  { icon: '📶', title: '多源融合', description: '融合物联网、大数据、传感等技术，实现多维度数据感知' },
  { icon: '🤖', title: '自主可控', description: '系统软硬件完全自主研发，安全可靠，技术可控' },
  { icon: '🔋', title: '高可靠性', description: '适用于高湿、高压、高电磁干扰等复杂隧道环境' },
  { icon: '🖥️', title: '平台可视化', description: '提供统一监测平台，实时显示照明与监测数据，支持云端分析' }
];

  const specifications = [
    { label: '防护等级', value: 'IP67' },
    { label: '运行速度', value: '≤1 m/s' },
    { label: '越障能力', value: '12 cm' },
    { label: '涉水深度', value: '40 cm' },
    { label: '续航时间', value: '5 h' },
    { label: '爬坡能力', value: '30°' },
    { label: '工作温度', value: '-20°C ~ 50°C' },
    { label: '通信方式', value: '无线 Wi‑Fi / 4G' },
    { label: '遥控距离', value: '≈60 m' },
    { label: '补光距离', value: '≥45 m' },
    { label: '外形尺寸', value: '1200 × 730 × 1010 mm（二代）' },
    { label: '整机重量', value: '约 170 kg（二代）' }
  ];

  const [showImage, setShowImage] = useState(true);
  const [imageSrc, setImageSrc] = useState('/Tunnel.png');

  const handleImageError = () => {
    setShowImage(false);
  };

  // 点击按钮在应用内切换到“宣传册”页
  const handleOpenBrochure = () => {
    if (typeof onOpenBrochure === 'function') {
      onOpenBrochure();
    }
  };

  return (
    <div className="product-info">
      <div className="hero-section">
        <h2>铁路隧道智慧照明与监测系统</h2>
        <p className="hero-description">
          铁路隧道智慧照明与监测系统将隧道照明与风险判识，安全监测预警相结合，实现智慧照明、节能绿色，同时覆盖隧道内及隧道口安全实时状态监测、重大风险及时预警。提高铁路隧道巡检维护的智能化水平，保障铁路安全运营。
        </p>
        {showImage && (
          <div className="hero-image">
            <div className="image-wrapper">
              <img
                src={imageSrc}
                alt="铁路隧道智慧照明与监测系统"
                loading="lazy"
                onError={handleImageError}
              />
            </div>
            <p className="image-caption">铁路隧道智慧照明与监测系统（示意图）</p>
          </div>
        )}
      </div>

      <div className="features-section">
        <h3>核心特性</h3>
        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h4>{feature.title}</h4>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="specifications-section">
        <h3>技术规格</h3>
        <div className="specs-grid">
          {specifications.map((spec, index) => (
            <div key={index} className="spec-item">
              <span className="spec-label">{spec.label}</span>
              <span className="spec-value">{spec.value}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="cta-section">
        <h3>立即体验</h3>
        <p>查看完整的产品宣传册，了解更多技术细节和应用案例</p>
        <button className="cta-button" onClick={handleOpenBrochure}>
          <span>📄</span>
          查看宣传册
        </button>
      </div>
    </div>
  );
};

export default ProductInfo;
